import { createRouter, createWebHistory } from "vue-router";
import CardList from "../views/CardList.vue";
import Vista from "../views/event/vista.vue";
import Detalles from "../views/event/detalles.vue";
import Ubicacion from "../views/event/ubicacion.vue";
import Comentarios from "../views/event/comentarios.vue";
import NotFound from "../views/NotFound.vue";
import NProgress from "nprogress";

const routes = [
	{
		path: "/",
		name: "CardList",
		component: CardList
	},
	{
		path: "/Character/:id",
		name: "EventLayout",
		props: true,
		component: EventLayout,
		children: [
			{
				path: "",
				name: "Detalles",
				component: Detalles
			},
			{
				path: "Ubicacion",
				name: "Ubicacion",
				component: Ubicacion
			},
			{
				path: "Comentarios",
				name: "comentarios",
				component: Comentarios
			}
		]
	},
	{
		path: "/ayuda",
		name: "ayuda",
		component: () =>
			import(/* webpackChunkName: "about" */ "../views/ayuda.vue")
	},
	{
		path: "/:catchAll(.*)",
		name: "Nada",
		component: Nada
	},
	{
		path: "/404/:resource",
		name: "404Resource",
		component: Nada,
		props: true
	}
];

const router = createRouter({
	history: createWebHistory(process.env.BASE_URL),
	routes
});

router.beforeEach((to, from, next) => {
	NProgress.start();
	next();
});

export default router;
