import { createRouter, createWebHistory } from "vue-router";
import Inicio from "../views/Inicio.vue";
import Vista from "../views/event/Vista.vue";
import Detalles from "../views/event/Detalles.vue";
import Ubicacion from "../views/event/Ubicacion.vue";
import Comentarios from "../views/event/Comentarios.vue";
import Episodios from "../views/event/Episodios.vue";
import Episodio from "../views/event/Episodio.vue";
import Nada from "../views/Nada.vue";
import NProgress from "nprogress";

const routes = [
	{
		path: "/",
		name: "Inicio",
		component: Inicio
	},
	{
		path: "/Character/:id",
		name: "Vista",
		props: true,
		component: Vista,
		children: [
			{
				path: "",
				name: "Detalles",
				component: Detalles
			},
			{
				path: "Ubicacion",
				name: "Ubicacion",
				component: Ubicacion
			},
			{
				path: "Comentarios",
				name: "Comentarios",
				component: Comentarios
			},
			{
				path: "Episodios",
				name: "Episodios",
				component: Episodios,
				children: [
					{
						path: "Episodio",
						name: "Episodio",
						component: Episodio
					}
				]
			}
		]
	},
	{
		path: "/ayuda",
		name: "ayuda",
		component: () =>
			import(/* webpackChunkName: "about" */ "../views/Ayuda.vue")
	},
	{
		path: "/:catchAll(.*)",
		name: "Nada",
		component: Nada
	},
	{
		path: "/404/:resource",
		name: "404Resource",
		component: Nada,
		props: true
	}
];

const router = createRouter({
	history: createWebHistory(process.env.BASE_URL),
	routes
});

router.beforeEach((to, from, next) => {
	NProgress.start();
	next();
});

export default router;
